(()=>{const e=document.getElementById("trail"),t=document.getElementById("menu");window.onmousemove=t=>{const n=t.target.closest(".interactable");!function(t,n){const o={transform:`translate(${t.clientX-e.offsetWidth/2}px, ${t.clientY-e.offsetHeight/2}px) scale(${n?.6:1})`};e.animate(o,{duration:800,fill:"forwards"})}(t,null!==n)},Array.from(document.getElementsByClassName("menu-item")).forEach(((e,n)=>{e.onmouseover=()=>{t.dataset.activeIndex=n}}))})();